import React from "react";
import ReactDOM from "react-dom";

const Header = (props) => {
  return (
    <div>
      <h1>{props.courseName.name}</h1>
    </div>
  );
};

const Part = (props) => {
  return (
    <div>
      <p>
        {props.partX} {props.exercisesX}
      </p>
    </div>
  );
};

const Content = (props) => {
  return (
    <div>
      <Part
        partX={props.parts.parts[0].name}
        exercisesX={props.parts.parts[0].exercises}
      />
      <Part partX={props.parts[1].name} exercisesX={props.parts[1].exercises} />
      <Part partX={props.parts[2].name} exercisesX={props.parts[2].exercises} />
    </div>
  );
};

const Total = (props) => {
  return (
    <div>
      <p>
        Number of exercises{" "}
        {props.parts[0].exercises +
          props.parts[1].exercises +
          props.parts[2].exercises}
      </p>
    </div>
  );
};

const App = () => {
  const course = {
    name: "Half Stack application development",
    parts: [
      {
        name: "Fundamentals of React",
        exercises: 10,
      },
      {
        name: "Using props to pass data",
        exercises: 7,
      },
      {
        name: "State of a component",
        exercises: 14,
      },
    ],
  };

  return (
    <div>
      <Header courseName={course} />
      <Content parts={course} />
      {/* <Total parts={course.parts} /> */}
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById("root"));
