import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Note from "./components/Note";

const App = () => {
    const [notes, setNotes] = useState([])
    const [newNote, setNewNote] = useState('')
    const [showAll, setShowAll] = useState(true)

    // Using eventHandler
    // useEffect(() => {
    //     console.log('effect')

    //     const eventHandler = response => {
    //         console.log('promise fulfilled')
    //         setNotes(response.data)
    //     }

    //     const promise = axios.get('http://localhost:3001/notes')
    //     promise.then(eventHandler)
    // }, [])

    // Using useEffect
    useEffect(() => {
        console.log('effect')
        axios
            .get('http://localhost:3001/notes')
            .then(response => {
                console.log('promise fulfilled')
                setNotes(response.data)
            })
    }, [])
    // console.log('render', notes.length, 'notes')

    // Rewriting useEffect
    // const hook = () => {
    //     console.log('effect')
    //     axios
    //         .get('http://localhost:3001/notes')
    //         .then(response => {
    //             console.log('promise fulfilled')
    //             setNotes(response.data)
    //         })
    // }

    // useEffect(hook, [])

    const addNote = event => {
        event.preventDefault() // The default action submits a form & cause the page to reload.
        // console.log('button clicked', event.target)
        const noteObject = {
            content: newNote,
            date: new Date().toISOString(),
            important: Math.random() < 0.5,
            id: notes.length + 1 // better to let the server generate ids for our resources
        }

        axios
            .post('http://localhost:3001/notes', noteObject)
            .then(response => {
                console.log(response)
                setNotes(notes.concat(response.data))
                setNewNote('')
            })

    }

    const toggleImportanceOf = id => {
        console.log('importance of ' + id + ' needs to be toggled')
    }

    const handleNoteChange = (event) => {
        // console.log(event.target.value)
        setNewNote(event.target.value)
    }

    const notesToShow = showAll
        ? notes
        : notes.filter(note => note.important) //  === true is redundant because note.important is either true or false

    return (
        <div>
            <h1>Notes</h1>
            <div>
                <button onClick={() => setShowAll(!showAll)}>
                    show {showAll ? 'important' : 'all'}
                </button>
            </div>
            <ul>
                {notesToShow.map((note, i) =>
                    <Note
                        key={i}
                        note={note}
                        toggleImportance={() => toggleImportanceOf(note.id)} />
                )}
            </ul>
            <form onSubmit={addNote}>
                <input
                    value={newNote}
                    onChange={handleNoteChange}
                />
                <button type="submit">save</button>
            </form>
        </div>
    );
};

export default App;
