import React from 'react';

const Notification = ({ message, isSuccessful = true }) => {
    const style = {
        notifySuccess: {
            border: '2px solid green',
            borderRadius: '2vh'
        },
        notifyFailure: {
            border: '2px solid red',
            borderRadius: '2vh'

        },

    }
    if (message === null) {
        return <></>
    } else if (isSuccessful) {
        return (
            <div style={style.notifySuccess}>
                {message}
            </div>
        )
    } else {
        return (
            <div style={style.notifyFailure}>
                {message}
            </div>
        )
    }


}

export default Notification;
