import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import loginService from './services/login'
import Notification from './components/Notification'
import LoginForm from './components/LoginForm'
import AddBlogForm from './components/AddBlogForm'


const App = () => {
  const [blogs, setBlogs] = useState([])
  const [user, setUser] = useState(null)
  const [errorMessage, setErrorMessage] = useState({ message: null, isSuccessful: true })
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')
  const [url, setUrl] = useState('')
  const [addBlogFormVisible, setAddBlogFormVisible] = useState(false)


  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs(blogs)
    )
  }, [])

  useEffect(() => {
    // Search for token in local storage
    const loggedUserJSON = window.localStorage.getItem('loggedUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])

  // Using async/await allows to get the outcome of the request 
  // before moving forward
  // e.g. login or show error message
  const handleLogin = async (event) => {
    event.preventDefault()
    try {
      // server response (inc. token & user details) is saved to user
      const user = await loginService.login({
        username, password,
      })

      // Save token in local storage
      window.localStorage.setItem(
        'loggedUser', JSON.stringify(user)
      )

      blogService.setToken(user.token)

      setUser(user)
      setUsername('')
      setPassword('')
    } catch (exception) {
      setErrorMessage({ message: 'Wrong credentials', isSuccessful: false })
      setTimeout(() => {
        setErrorMessage({ message: null })
      }, 5000)
    }
  }


  const handleAddBlog = async (event) => {
    event.preventDefault()
    let newBlog = {
      title: title,
      author: author,
      url: url
    }
    try {
      const returnedBlog = await blogService.create(newBlog)
      setBlogs(blogs.concat(returnedBlog))

      setTitle('')
      setAuthor('')
      setUrl('')
      setErrorMessage({ message: `A new blog: ${newBlog.title}`, isSuccessful: true })
      setTimeout(() => {
        setErrorMessage({ message: null })
      }, 5000)
    } catch (exception) {
      setErrorMessage({ message: `Title and Url are necessary`, isSuccessful: false })
      setTimeout(() => {
        setErrorMessage({ message: null })
      }, 5000)
    }

  }


  // Alternative to two return
  // { user === null && loginForm() }
  // { user !== null && noteForm() }

  if (user === null) {
    return (
      <>
        <Notification message={errorMessage} />
        <LoginForm
          username={username}
          setUsername={setUsername}
          password={password}
          setPassword={setPassword}
          handleLogin={handleLogin}
        />
      </>
    )
  }

  return (
    <div>
      <Notification message={errorMessage} />
      <AddBlogForm
        user={user}
        setUser={setUser}
        handleAddBlog={handleAddBlog}
        title={title}
        setTitle={setTitle}
        author={author}
        setAuthor={setAuthor}
        url={url}
        setUrl={setUrl}
      />
      {blogs.map(blog =>
        <Blog key={blog.id} blog={blog} />
      )}
    </div>
  )
}

export default App
