import React from 'react';

const Notification = ({ message, isSuccessful = true }) => {
    const style = {
        notifySuccess: {
            border: '2px solid green',

        },
        notifyFailure: {
            border: '2px solid red',
        },
        popupStyle: {
            borderRadius: '0.5vh',
            backgroundColor: '#d1d1e0'
        }
    }
    if (message === null) {
        return <></>
    } else if (isSuccessful) {
        return (
            <div style={style.notifySuccess, style.popupStyle}>
                {message}
            </div>
        )
    } else {
        return (
            <div style={style.notifyFailure, style.popupStyle}>
                {message}
            </div>
        )
    }


}

export default Notification;
