import axios from 'axios'
const baseUrl = '/api/blogs'

let token = null

const setToken = newToken => {
  token = `bearer ${newToken}`
}

const getAll = () => {
  const request = axios.get(baseUrl)
  return request.then(response => response.data)
}

const create = async newObject => {
  const config = {
    headers: { Authorization: token },
  }

  const response = await axios.post(baseUrl, newObject, config)
  return response.data


  // console.log('response', response);
}

const update = async (id, changedBlog) => {
  const config = {
    headers: { Authorization: token },
  }

  const { id: _notUsedId, ...payload } = changedBlog
  payload.user = payload.user.id

  console.log('id in update: ', id)
  // console.log('newObject in update: ', newObject)

  console.log(`backend address: ${baseUrl}/${id}`);
  const request = await axios.put(`${baseUrl}/${id}`, payload, config)
  console.log('request in update: ', request)

  return request.then(response => response.data)
}


export default { getAll, create, update, setToken }
