import React, { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import loginService from './services/login'
import LoginForm from './components/LoginForm'
import Notification from './components/Notification'

const App = () => {
  const [blogs, setBlogs] = useState([])
  const [user, setUser] = useState(null)
  const [errorMessage, setErrorMessage] = useState(null, true)
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [title, setTitle] = useState('')
  const [author, setAuthor] = useState('')
  const [url, setUrl] = useState('')


  useEffect(() => {
    blogService.getAll().then(blogs =>
      setBlogs(blogs)
    )
  }, [])

  useEffect(() => {
    // Search for token in local storage
    const loggedUserJSON = window.localStorage.getItem('loggedUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])

  const handleLogin = async (event) => {
    event.preventDefault()
    console.log('logging in with', username, password)

    try {
      // server response (inc. token & user details) is saved to user
      const user = await loginService.login({
        username, password,
      })

      // Save token in local storage
      window.localStorage.setItem(
        'loggedUser', JSON.stringify(user)
      )

      blogService.setToken(user.token)
      setUser(user)
      setUsername('')
      setPassword('')
    } catch (exception) {
      setErrorMessage('Wrong credentials', false)
      setTimeout(() => {
        setErrorMessage(null)
      }, 5000)
    }
  }


  const handleAddBlog = (event) => {
    event.preventDefault()

    let newBlog = {
      title: title,
      author: author,
      url: url
    }
    blogService
      .create(newBlog)
      .then(savedBlog => console.log('savedBlog', savedBlog))
    // .then(savedBlog => setBlogs(blogs.concat(savedBlog)))


    setTitle('')
    setAuthor('')
    setUrl('')
    setErrorMessage(`A new blog: ${newBlog.title}`, false)
    setTimeout(() => {
      setErrorMessage(null)
    }, 5000)
  }


  // Alternative to two return
  // { user === null && loginForm() }
  // { user !== null && noteForm() }

  if (user === null) {
    return (
      <LoginForm
        username={username}
        setUsername={setUsername}
        password={password}
        setPassword={setPassword}
        handleLogin={handleLogin}
      />
    )
  }

  const cleanCredentials = () => {
    window.localStorage.removeItem('loggedUser')
    setUser(null)
  }

  return (
    <div>
      <Notification message={errorMessage} />
      <h2>Blogs</h2>
      {console.log('user, ', user)}
      <p>{user.name} logged-in</p>
      <button onClick={cleanCredentials}>log out</button>
      <form onSubmit={handleAddBlog}>
        <div>
          title
        <input
            type='text'
            value={title}
            name='title'
            onChange={({ target }) => setTitle(target.value)}
          />
        </div>
        <div>
          author
        <input
            type='text'
            value={author}
            name='author'
            onChange={({ target }) => setAuthor(target.value)}
          />
        </div>
        <div>
          url
        <input
            type='text'
            value={url}
            name='url'
            onChange={({ target }) => setUrl(target.value)}
          />
        </div>
        <button type="submit">create</button>
      </form>
      {blogs.map(blog =>
        <Blog key={blog.id} blog={blog} />
      )}
    </div>
  )
}

export default App
