const initialState = { message: null, isSuccessful: true }

const notificationReducer = (state = initialState, action) => {

  switch (action.type) {
    case 'NOTIFY':
      return action.payload
    default:
      return state
  }

}

const notifyUser = (message) => {
  return {
    type: 'NOTIFY',
    payload: message
  }
}

export default notificationReducer;
